import React from 'react';
import '../../style/common/common.scss';
import './wallet.scss';
import logo from '../../logo.svg';
import { getWalletInfo } from '../../services/apis';


class Wallet extends React.Component {


  constructor() {
    super();
    this.state = {
      showLoader: false,
      networkproblem: false,
      wallet_balance:0,
      transactions: [],
      withdrawals: [],
      wip_info :{count:0,amt:0}
    }
  }

  componentDidMount = () => {
    const that = this
    const result = getWalletInfo();
    result.then(function (res) {
      if (res.data) {
        const data = res.data
        let   wallet_balance = 0, transactions = [], withdrawals = [];
        let wallet_balanceL = data.wallet_balance, transactionsL = data.transactions, withdrawalsL = data.withdrawals;
        if( wallet_balanceL && wallet_balanceL>0 ) wallet_balance = wallet_balanceL;
        if( transactionsL && transactionsL.length>0) transactions = transactionsL;
        if(withdrawalsL && withdrawalsL.length>0) withdrawals = withdrawalsL;
        that.setState({ wallet_balance, transactions, withdrawals });
      }
      else {
        // that.NetworkError();
      }
    })
      .catch(function (error) {
        // that.NetworkError();
      });
  }


    render() {
      const that = this
    return ( <div className="wallet">
        <div className="top_sec center dispFlex">
          <i className="arrow left back_arrow"></i>
          <div className='header_title dispFlex'> Trasaction History 
        </div>
      </div>
      <div className="content_sec">
          <div className="dispFlex balance_card">
            <div className="top">TOTAL BALANCE<span>&nbsp;</span> <span className="rupee"></span>{that.state.wallet_balance}</div>
            <div className="bottom">{that.state.wip_info.count} withdrawal in progress</div>
          </div>
          <div className="ui_card dispFlex wip_card">
            <div className="contest_category_img"> <img src={logo}></img> </div>
            <div className="details_sec">
              <div className="top">
                 <span className="rupee"></span>{that.state.wip_info.amt}<span>&nbsp;</span> withdrawal in progress
              </div>
              <div className="bottom"> withdrawan from your winnings</div>
            </div>
          </div>
          <div className="ui_card dispFlex txn_card">
            <div className="top">
                 <div className="amt_sec"><span className="rupee"></span>{that.state.wip_info.amt}<span>&nbsp;</span> withdrawal in progress</div>
                 <div className="contest_category_img"> <img src={logo}></img> </div>
            </div>
            <div className="bottom"> Added Money Successfully</div>
          </div>
          {/* {that.showContestCategories(that.state.contest_types)}
          {that.showContestDetails(that.state.contestsToShow)} */}
        </div>
      </div>)
    }
  }

  export default Wallet;
