import React from 'react';
import './contest.scss';
import '../../style/common/common.scss';
import logo from '../../logo.svg';
import inr_ico from '../../img/common/rupee-indian.svg';
import { getContests } from '../../services/apis';

class Contest extends React.Component {

  constructor() {
    super();
    this.state = {
      showLoader: false,
      networkproblem: false,
      contests: [],
      contestsToShow: [],
      isAllActive: true,
      active_contest_type:''
    }
  }

  componentDidMount = () => {
    const that = this
    const result = getContests();
    result.then(function (res) {
      if (res.data) {
        const data = res.data
        let  contests = [], contestsToShow = [];
        contests = data.contests;
        contestsToShow = contests;
        that.setState({ contests, contestsToShow });
      }
      else {
        // that.NetworkError();
      }
    })
      .catch(function (error) {
        // that.NetworkError();
      });
  }



    render() {
      const that = this;
      return <div>
        <div className="top_sec dispFlex">
          <i class="arrow left"></i>
          <div className='header_title'> <span>Choose a contest </span>
          <span>01h 58m 17s </span></div>
          {/* {that.showCards(that.state.banners)} */}
        </div>
        <div className="content_sec">
          {/* {that.showContestCategories(that.state.sports)} */}
          {/* {that.showContestDetails(that.state.challengesToShow)} */}
        </div>
  
      </div>;
    }
  }

  export default Contest;
