import * as types from '../actions/action-types';
// import _ from 'lodash';

const initialState = {
    // SAVEV3 RESPONSE OR AJAX RESPONSE ON CHECKOUT
    order: [],
    // TO BE SHOWN ON PAYMENT INFO
    orderDet: {  totalFare: 0, loyaltyFare: 0, onwardFare: 0, returnFare: 0, taxes: 0, providerDiscount: 0, couponDiscount: 0, totalDiscount: 0, reliability: 0, couponCode: '', isReliability: true, couponMsg: '', isCoupon: 0, couponArr: [], couponBreakup: {exclusiveOffers: 0, previlege: 0, coupons: 0} },
    // CONTACT INFO SAVED IN ORDER ABOVE AS CUSTOMER DETAILS // NO STD
    infoSaved: true,
    // CARD DETAILS
    ccDetails: [], 
    ccName: '',
    ccNo: '',
    ccMonth: '',
    ccYear: '',
    ccCvv: '',
    ccSaveCvv: '',
    ccinfoprovider: '',
    ccinfotype:'all',
    isSavedCard: true,
    isValidCard: false,
    isValidCvv: false,
    juspay_token: '',
    epaypayer :'',
    // SAVED PAYMENT DETAILS
    nb: '',
    wallet: '',
    paypal: false,
    dac: '',
    epay: 'epay',
    amazonpay: 'amazonpay',
    upi: 'upi',
    op: 'paypal_int',
    pgType:"cc",
    pgMethod :"",
    sectionToView :"pg",
    appliedPgOffer:"",
    vpa:""
};

const checkoutReducer = function(state = initialState, action) {
    let order = state.order;
    let orderDet = state.orderDet;
    switch(action.type) {
        case types.GET_CHECKOUT_SUCCESS:
            return Object.assign({}, state, { order: action.order });
        case types.PARSE_CHECKOUT_DATA:
            return Object.assign({}, state, { orderDet: action.data });
        case types.UPDATE_TOTAL_FARE:
            orderDet.totalFare = action.data;
            return Object.assign({}, state, { orderDet });
        case types.UPDATE_LOYALTY_FARE:
            orderDet.totalFare = action.total;
            orderDet.loyaltyFare = action.loyalty;
            return Object.assign({}, state, { orderDet });
        case types.UPDATE_COUPON_APPLIED:
            orderDet.totalFare = action.fare;
            orderDet.couponDiscount = action.coupon;
            orderDet.totalDiscount = action.coupon;
            orderDet.couponCode = action.isCoupon ? action.couponCode : '';//orderDet.couponCode;
            orderDet.couponArr = action.couponArr;
            orderDet.couponBreakup = action.couponBreakup;
            orderDet.couponMsg = action.couponMsg;
            orderDet.isCoupon = action.isCoupon;
            return Object.assign({}, state, { orderDet });
        case types.UPDATE_COUPON_CODE:
            orderDet.couponCode = action.couponCode;
            return Object.assign({}, state, { orderDet });
        case types.UPDATE_CONTACT_INFO:
            order.customer_details.email = action.email;
            order.customer_details.contactNo = action.contactNo;
            return Object.assign({}, state, { order });
        case types.IS_RELIABILITY:
            orderDet.isReliability = action.data;
            return Object.assign({}, state, { orderDet });
        case types.IS_INFO_SAVED:
            return Object.assign({}, state, { infoSaved: action.data });
        case types.GET_CARD_NO:
            return Object.assign({}, state, { ccNo: action.no, isValidCard: action.isValidNo, isValidCvv: action.isValidCvv });
        case types.GET_SAVED_CARD:
            return Object.assign({}, state, { ccDetails: action.data });
        case types.GET_CARD_NAME:
            return Object.assign({}, state, { ccName: action.name });
        case types.GET_CARD_MONTH:
            return Object.assign({}, state, { ccMonth: action.month });
        case types.GET_CARD_YEAR:
            return Object.assign({}, state, { ccYear: action.year });
        case types.GET_CARD_CVV:
            return Object.assign({}, state, { ccCvv: action.cvv, isValidCvv: action.isValidCvv });
        case types.GET_SAVED_CARD_CVV:
            return Object.assign({}, state, { ccSaveCvv: action.cvv });
        case types.GET_CARD_DET:
            return Object.assign({}, state, { ccinfoprovider: action.ccinfoprovider, ccinfotype: action.ccinfotype });
        case types.IS_SAVED_CARD:
            return Object.assign({}, state, { isSavedCard: action.data });
        case types.SEL_NB:
            return Object.assign({}, state, { nb: action.data,pgMethod:action.data });
        case types.SEL_WALLET:
            return Object.assign({}, state, { wallet: action.data,pgMethod:action.data });
        case types.SEL_DAC:
            return Object.assign({}, state, { dac: action.data });
        case types.SEL_PAYPAL:
            return Object.assign({}, state, { paypal: action.data,pgMethod:action.data });
        case types.SET_EPAY_PAYER:
            return Object.assign({}, state, {epaypayer: action.data,pgMethod:action.data });
        case types.SECTION_TO_VIEW:
            return Object.assign({}, state, { sectionToView: action.data });   
        case types.SET_PG_METHOD:
            return Object.assign({}, state, { pgMethod: action.pgMethod,pgType:action.pgType });
        case types.SET_PG_SUB_OPTION:
            return Object.assign({}, state, { pgMethod: action.data});
        case types.APPLY_PG_OFFER:
            return Object.assign({}, state, { appliedPgOffer: action.data }); 
        case types.SET_VPA:
            return Object.assign({}, state, { vpa: action.data });                                    
        default:
            return state;
    }
}

export default checkoutReducer;
