import React from 'react';
import './home.scss';
import '../../style/common/common.scss';
import logo from '../../logo.svg';
import inr_ico from '../../img/common/rupee-indian.svg';
import timer_ico from '../../img/common/timer.svg';
import {getHomeData} from '../../services/apis'


class Home extends React.Component {

  constructor()
  {
      super();
      this.state ={
            showLoader:false,
            networkproblem:false,
            banners:[],
            sports:[],
            challenges:[]
      }
  }

  componentDidMount = () => {

    const result = getHomeData();
    result.then(function(res){
        if(res.data)
        {
            // that.manageBookingData(res.data.response);
            console.log(res.data)
        }
        else
        {
            // that.NetworkError();
        }
    })
    .catch(function(error){
        // that.NetworkError();
    });
  }

  showCards = () => {
    return <div className="cards_sec">
              <div className="ui_card ui_card1"> <img src="https://cdn-images.spcafe.in/img/es3/articles/Banners_App/DIWALI_BANNER.jpg"></img> </div>
              <div className="ui_card ui_card1"> <img src="https://cdn-images.spcafe.in/img/es3/articles/Twitter1.jpg"></img> </div> 
              <div className="ui_card ui_card1"> <img src="https://cdn-images.spcafe.in/img/sportscafeimages/Game/Banner_Ads/feature/Bingo_banner.png"></img> </div>
          </div>
  }

  showContestCategories = () => {
    return <div className="contest_categories">
      <div className="contest_category active">
        <div className="contest_category_img">
          <img src={logo}></img>
        </div>
        <div className="contest_category_name">All</div>
      </div>
      <div className="contest_category">
        <div className="contest_category_img">
          <img src={logo}></img>
        </div>
        <div className="contest_category_name">CRICKET</div>
      </div>
      <div className="contest_category">
        <div className="contest_category_img">
          <img src={logo}></img>
        </div>
        <div className="contest_category_name">FOOTBALL</div>
      </div>
      <div className="contest_category">
        <div className="contest_category_img">
          <img src={logo}></img>
        </div>
        <div className="contest_category_name">BASKETBALL</div>
      </div>
      <div className="contest_category">
        <div className="contest_category_img">
          <img src={logo}></img>
        </div>
        <div className="contest_category_name">TENNIS</div>
      </div>
    </div>
  }

  showContestDetails = () => {
    return <div className="contest_details_sec">
      <div className="title">ALL<span className="noC">(30)</span></div>
      <div className="contest_cards">
        <div className="contest_card ui_card ui_card2">
          <div className="contest_top">Picks</div>
          <div className="contest_mid">
            <img className="party_img" src="https://cdn-images.spcafe.in/img/es3-cfit-s150/articles/Nostragamus-Game/Team_Tournament_Logo_Emblem/Cricket/Team_Logo_Emblem/southafrica.png"></img>
            <div className="match_details">
              <div className="challenge_name">
                <span className="party_short_name txt_right">SA</span> <span> VS </span><span className="party_short_name txt_left">AUS</span>
              </div>
              <div className="challenge_details">
                <div className="desc txt_right">Test Day 3</div>
                <div className="live_dot"><div></div></div>
                <div className="txt_left">Live Score Updates</div>
              </div>
            </div>
            <img className="party_img right" src="https://cdn-images.spcafe.in/img/es3-cfit-s150/articles/Nostragamus-Game/Team_Tournament_Logo_Emblem/Cricket/Team_Logo_Emblem/australia.png"></img>
          </div>
          <div className="contest_bot">
            <div className="money_sec">
              <img className="curncy_ico" src={inr_ico}></img>
              <div className="prize_money">99000</div>
            </div>
            <div className="time_sec">
              <img className="timer_ico" src={timer_ico}></img>
              <div className="start_time">22h 58m</div>
            </div>
          </div>
        </div>
      </div>
    </div>
  }

  render() {
    return <div>
      <div className="top_sec">
        <div className='header_title center'> Home </div>
        {this.showCards()}
      </div>
      <div className="content_sec">
        {this.showContestCategories()}
        {this.showContestDetails()}
      </div>

    </div>;
  }
}

export default Home;
